Reveal.initialize({

    /*Definicion de los Plugins*/ 
    plugins: [ RevealMarkdown, RevealHighlight  ],

    /* auto reproduccion del video*/    
    autoPlayMedia: true,
    /* numero de slide */
    slideNumber: 'true',
    /*AutoSlide*/
    autoSlide: 93000,
    loop: true,
    /*    autoSlideMethod: Reveal.navigateRight,*/
    /*transition por defecto para todos los slides*/
    backgroundTransition: 'slide'
  });

  /*Options
  Data Transition: [none, fade, zoom, slide, covex, concave]
  */